.data
puzzle_test: .asciiz "ULL
RRDDD
LURDL
UUUUD"

puzzle: .asciiz "



"

intro_text: .asciiz "Part 1: "

.text
main:
        la      $a0, intro_text
        jal     print_string
        la      $s0, puzzle     # s0 = puzzle pointer
        li      $s2, 4          # s2 = current button idx
loop:
        lb      $a0, 0($s0)
        beqz    $a0, final_button
        beq     $a0, '\n', found_button
        beq     $a0, 'U', up
        beq     $a0, 'L', left
        beq     $a0, 'R', right
down:
        bgt     $s2, 5, loop_continue
        add     $s2, $s2, 3
        j       loop_continue
left:
        rem     $t0, $s2, 3
        beq     $t0, 0, loop_continue
        sub     $s2, $s2, 1
        j       loop_continue
up:
        blt     $s2, 3, loop_continue
        sub     $s2, $s2, 3
        j       loop_continue
right:
        rem     $t0, $s2, 3
        beq     $t0, 2, loop_continue
        add     $s2, $s2, 1
loop_continue:
        add     $s0, $s0, 1
        j       loop
found_button:
        add     $a0, $s2, 1
        jal     print_int_and_space
        j       loop_continue
final_button:
        add     $a0, $s2, 1
        jal     print_int_and_space
done:
        j done